INSERT INTO `heka` (`id`, `f`, `t`, `b`, `c`, `0`) VALUES
(7, '地方', '3', '/res/xheka/3/t1.gif', '春回大地，万物复苏，明媚春光挡不住；春归此处，万紫千红，美丽鲜花满天涯；春来送暖，万山缤纷，百草发芽共争春；春风春雨，万缕千丝，冷暖悲喜心自知。立春到来，万般精彩，愿你快乐展颜，心情自由自在，温暖的感觉胸中藏，幸福的花儿心上开！', 'presend'),
(8, '地方', '7', '/res/xheka/7/t1.gif', '日月轮转永不断，情若真挚长相伴，幸福连连不间断，关键祝福常出现，问候挂牵到永远，生日之际送祝愿，愿你生日多欢颜，笑对生活每一天！生日快乐！', 'presend'),
(9, '放到', '9', '/res/xheka/9/t8.gif', '不论何时何刻，只要你需要我，我都会及时出现在你身边，我可能没法陪你度过每一个快乐的时刻，但我会分担你所有的伤悲。', 'presend'),
(10, 'df', '6', '/res/xheka/6/t1.gif', '可爱的你偷走我的情、盗走我的心，我决定告你上法庭，该判你什么罪呢？法官翻遍所有的犯罪记录和案例，最后陪审团一致通过：判你终生归我。', 'presend'),
(11, '1', '1', '/res/xheka/1/t3.gif', '炮竹声声春来报，春风得意喜鹊叫。人欢马叫春节到，美酒飘香闹春宵。合家团圆欢声笑，幸福生活节节高。愿君健康福禄寿，举杯贺岁到晨晓。祝春节快乐！', 'presend'),
(12, 'q', '5', '/res/xheka/5/t2.gif', '满天星光，最爱你许过愿望的一颗；鲜花绽放，最爱你摘下微笑的一朵；曼妙旋律，最爱你感动落泪的一段。情人节快乐！爱你。', 'presend'),
(13, 'q', NULL, '/res/xheka/5/t8.gif', '2.14情人节：我把爱写在字里，我把情留在行间，我把玫瑰送给圣洁，我把真爱表达给真情。我爱你，祝你情人节快乐！', 'presend'),
(14, 'q', NULL, '/res/xheka/5/t4.gif', '^o^满天星光，最爱你许过愿望的那一颗，鲜花绽放，最爱你摘下微笑的那一朵，曼妙旋律，最爱你感动落泪的那一段，亲爱的，情人节快乐，爱你。', 'presend'),
(15, 'q', '1', '/res/xheka/1/t1.gif', '贺卡把年拜，收到笑颜开；马年好运来，保准你发财；健康又实在，做人有情怀；亲情友情在；异性更喜爱；祝福都爱戴，保准你乐坏。祝你新年愉快！!', 'presend'),
(16, 'q', '6', '/res/xheka/6/t1.gif', '如果上天让我许三个愿望，第一个是今生今世和你在一起，第二个是来生来世和你在一起，第三个是永生永世和你不分离。如果你走了，我会在泪水里爱你；如果你的心死了，我会在生命里爱你；如果生命消失了，我会在轮回里爱你；如果世界消失了，我会在天堂里爱你。', 'presend'),
(17, 'q', '4', '/res/xheka/4/t1.gif', '千万个祝愿，在寒风中凝固，但元宵节的灯笼，元宵节的汤圆，元宵节的贺卡祝福却在温暖中传送，愿温馨、温情、温柔在祝福中停留，祝你元宵节快乐！', 'presend'),
(18, 'q', '9', '/res/xheka/9/t5.gif', '人在旅途,难免会遇到荆棘和坎坷,但风雨过后,一定会有美丽的彩虹.我希望看到一个坚强的我，更希望看到一个坚强的你！！！', 'presend'),
(19, 'jj', '4', '/res/xheka/4/t1.gif', '喜迎元宵送你“三大元”：一送你，团圆的“圆”，愿你马上美事圆圆；二送你，财源的“源”，愿你四季有财源；三送你，缘分的“缘”，愿你新年逢佳缘。', 'presend'),
(20, '222', '2', '/res/xheka/2/t2.gif', '祝您在新的一年里：一家和和睦睦，一年开开心心;一生快快乐乐，一世平平安安;天天精神百倍，月月喜气洋洋;年年财源广进，岁岁平安祥和!新年快乐!', 'presend'),
(21, '摇一摇', '5', '/res/xheka/5/t11.gif', '2.14情人节：我把爱写在字里，我把情留在行间，我把玫瑰送给圣洁，我把真爱表达给真情。我爱你，祝你情人节快乐！', 'presend'),
(22, '89', '8', '/res/xheka/8/t3.gif', '愿耶和华从至圣所赐福于你，愿你们一生一世都看见圣城耶路撒冷的好处！', 'presend'),
(23, '厕所', '4', '/res/xheka/4/t8.gif', '彩灯点缀世界的华彩乐章，汤圆包裹甜蜜的团团圆圆。家是大大的同心圆，把我们的心紧紧相牵。一个美好的夜晚，祝你快乐到永远。元宵节快乐！', 'presend'),
(24, '摇一摇', '3', '/res/xheka/3/t4.gif', '^o^立春到，让春风吹顺你的事业，春光照亮你的前途，春雨滋润你的生活，春意点缀你的心情，春花灿烂你的笑容，祝春风得意！立春快乐！', 'presend'),
(25, '方法', '6', '/res/xheka/6/t1.gif', '可爱的你偷走我的情、盗走我的心，我决定告你上法庭，该判你什么罪呢？法官翻遍所有的犯罪记录和案例，最后陪审团一致通过：判你终生归我。', 'presend'),
(26, '可能', '2', '/res/xheka/2/t3.gif', '希望春天的花常开不败，希望夏天的水长流不息，希望秋天的枫叶红透你心，希望冬天的雪花纯洁如你，更希望忙忙碌碌的你，永远快乐、幸福！新年快乐！', 'presend'),
(27, '你好', NULL, '/res/xheka/2/t10.gif', '酒，越酿越醇厚;茶，越久越清香;朋友，越走越真诚;祝福，越早越真挚。马年到来，提前拜年，祝你度过一个快乐而开心的新年!', 'presend'),
(28, '你好', NULL, '/res/xheka/2/t3.gif', '把有些事不会因时光流逝而褪去，有些人不会因不常见面而忘记，在我心里你是我永远的朋友。在新年到来之际，恭祝好友酷炫十足！', 'presend');
